(19, '3', 300, 187, '&lt;strong&gt;Redenatie: &lt;/strong&gt;stilstaande of zeer langzaam stromende delen worden gebruikt als opgroeihabitat [{link;reference;301;301}, {link;reference;325;325}, {link;reference;269;269}, {link;reference;310;310}, {link;reference;309;309}, {link;reference;326;326}].'),
